function varargout = iqmod(varargin)
% Generate I/Q modulation waveform
% Parameters are passed as property/value pairs. Properties are:
% 'sampleRate' - sample rate in Hz
% 'numSymbols' - number of symbols
% 'modType' - modulation type (BPSK, QPSK, OQPSK, QAM4, QAM16, QAM64, QAM256)
% 'oversampling' - oversampling rate
% 'filterType' - pulse shaping filter ('Raised Cosine','Square Root Raised Cosine','Gaussian')
% 'filterNsym' - number of symbols for pulse shaping filter
% 'filterBeta' - Alpha/BT for pulse shaping filter
% 'carrierOffset' - frequency of carriers (can be a scalar or vector)
% 'magnitude' - relative magnitude (in dB) for the individual carriers
% 'newdata' - set to 1 if you want separate random bits to be generated for each carrier
% 'correction' - apply amplitude correction stored in iqampCorr()
% 'quadErr' - quadrature error in degrees
% 'plotConstellation' - to plot the constellation diagram
% 'channelMapping' - channel mapping array (format see iqdownload)
%
% If called without arguments, opens a graphical user interface to specify
% parameters
%
% Thomas Dippon, Keysight Technologies 2011-2016
%
% Disclaimer of Warranties: THIS SOFTWARE HAS NOT COMPLETED KEYSIGHT'S FULL
% QUALITY ASSURANCE PROGRAM AND MAY HAVE ERRORS OR DEFECTS. KEYSIGHT MAKES 
% NO EXPRESS OR IMPLIED WARRANTY OF ANY KIND WITH RESPECT TO THE SOFTWARE,
% AND SPECIFICALLY DISCLAIMS THE IMPLIED WARRANTIES OF MERCHANTABILITY AND
% FITNESS FOR A PARTICULAR PURPOSE.
% THIS SOFTWARE MAY ONLY BE USED IN CONJUNCTION WITH KEYSIGHT INSTRUMENTS. 

if (nargin == 0)
    iqmod_gui;
    return;
end
if (nargout >= 1)
    varargout{1} = [];
end
if (nargout >= 2)
    varargout{2} = [];
end
if (nargout >= 3)
    varargout{3} = [];
end
if (nargout >= 4)
    varargout{4} = [];
end
if (nargout >= 5)
    varargout{5} = [];
end
sampleRate = 4.2e9;
numSymbols = 256;
data = 'Random';
modType = 'QAM16';
oversampling = 4;
filterType = 'Root Raised Cosine';
filterNsym = 8;
filterBeta = 0.35;
filename = '';
dataContent = [];
carrierOffset = 0;
magnitude = 0;
quadErr = 0;
iqskew = 0;
gainImb = 0;
newdata = 1;
correction = 0;
normalize = 1;
plotConstellation = 0;
threshold = 1000000;
savefile = [];
arbConfig = [];
fct = 'display';
channelMapping = [1 0; 0 1];
hMsgBox = [];
i = 1;
while (i <= nargin)
    if (ischar(varargin{i}))
        switch lower(varargin{i})
            case 'samplerate';     sampleRate = varargin{i+1};
            case 'numsymbols';     numSymbols = varargin{i+1};
            case 'modtype';        modType = varargin{i+1};
            case 'data';           data = varargin{i+1};
            case 'datacontent';    dataContent = varargin{i+1};
            case 'filename';       filename = varargin{i+1};
            case 'oversampling';   oversampling = varargin{i+1};
            case 'filtertype';     filterType = varargin{i+1};
            case 'filternsym';     filterNsym = varargin{i+1};
            case 'filterbeta';     filterBeta = varargin{i+1};
            case 'carrieroffset';  carrierOffset = varargin{i+1};
            case 'magnitude';      magnitude = varargin{i+1};
            case 'quaderr';        quadErr = varargin{i+1};
            case 'iqskew';         iqskew = varargin{i+1};
            case 'gainimbalance';  gainImb = varargin{i+1};
            case 'newdata';        newdata = varargin{i+1};
            case 'correction';     correction = varargin{i+1};
            case 'normalize';      normalize = varargin{i+1};
            case 'plotconstellation'; plotConstellation = varargin{i+1};
            case 'arbconfig';      arbConfig = varargin{i+1};
            case 'function';       fct = varargin{i+1};
            case 'threshold';      threshold = varargin{i+1};
            case 'savefile';       savefile = varargin{i+1};
            case 'channelmapping'; channelMapping = varargin{i+1};
            case 'hmsgbox';        hMsgBox = varargin{i+1};
            otherwise error(['unexpected argument: ' varargin{i}]);
        end
    else
        error('string argument expected');
    end
    i = i+2;
end

%% create a modulator object
offsetmod = 0;
iscpm = 0;
clear hmod;
%
% the xxx_OLD modulation schemes don't work in MATLAB 2018b any longer
% I left them in the code so that backward compatibility can be verified
switch upper(modType)
    case 'BPSK_OLD';   hmod = modem.pskmod('M', 2);
    case 'BPSK';   hmod.m = 2; hmod.modulate = @(x) pskmod(x, hmod.m);
    case 'BPSK_X_OLD'; hmod = modem.pskmod('M', 2, 'PhaseOffset', pi/2);
    case 'BPSK_X'; hmod.m = 2; hmod.modulate = @(x) pskmod(x, hmod.m, pi/2);
    case 'QPSK_OLD';   hmod = modem.pskmod('M', 4, 'PhaseOffset', pi/4, 'SymbolOrder', 'Gray');
    case 'QPSK';   hmod.m = 4; hmod.modulate = @(x) pskmod(x, hmod.m, pi/4, 'Gray'); 
    case 'OQPSK_OLD';  hmod = modem.pskmod('M', 4, 'PhaseOffset', pi/4, 'SymbolOrder', 'Gray'); offsetmod = 1;
    case 'OQPSK';  hmod.m = 4; hmod.modulate = @(x) pskmod(x, hmod.m, pi/4, 'Gray'); offsetmod = 1;
    case '8-PSK_OLD';  hmod = modem.pskmod('M', 8, 'PhaseOffset', pi/8);
    case '8-PSK';  hmod.m = 8; hmod.modulate = @(x) pskmod(x, hmod.m, pi/8);
    case 'QAM4_OLD';   hmod = modem.qammod('M', 4);
    case 'QAM4';   hmod.m = 4; hmod.modulate = @(x) genqammod(x, [-1+1i;-1-1i;1+1i;1-1i]);
    case 'QAM16_OLD';  hmod = modem.qammod('M', 16, 'SymbolOrder', 'user-defined', 'SymbolMapping', [3 7 11 15 2 6 10 14 1 5 9 13 0 4 8 12]);
    case 'QAM16';  hmod.m = 16; hmod.modulate = @(x) genqammod(x, [-3+3i;-3+1i;-3-1i;-3-3i;-1+3i;-1+1i;-1-1i;-1-3i;1+3i;1+1i;1-1i;1-3i;3+3i;3+1i;3-1i;3-3i]);
    case 'QAM32_OLD';  hmod = modem.qammod('M', 32, 'SymbolOrder', 'user-defined', 'SymbolMapping', [3 2 30 31 9 15 21 27 8 14 20 26 7 13 19 25 6 12 18 24 5 11 17 23 4 10 16 22 0 1 29 28]);
    case 'QAM32';  hmod.m = 32; hmod.modulate = @(x) genqammod(x, [-3+5i;-1+5i;-1-5i;-3-5i;-5+3i;-5+1i;-5-1i;-5-3i;-3+3i;-3+1i;-3-1i;-3-3i;-1+3i;-1+1i;-1-1i;-1-3i;1+3i;1+1i;1-1i;1-3i;3+3i;3+1i;3-1i;3-3i;5+3i;5+1i;5-1i;5-3i;3+5i;1+5i;1-5i;3-5i]);
    case 'QAM64_OLD'; hmod = modem.qammod('M', 64, 'SymbolOrder', 'user-defined', 'SymbolMapping', [7 15 23 31 39 47 55 63 6 14 22 30 38 46 54 62 5 13 21 29 37 45 53 61 4 12 20 28 36 44 52 60 3 11 19 27 35 43 51 59 2 10 18 26 34 42 50 58 1 9 17 25 33 41 49 57 0 8 16 24 32 40 48 56]);
    case 'QAM64';  hmod.m = 64; hmod.modulate = @(x) genqammod(x, [-7+7i;-7+5i;-7+3i;-7+1i;-7-1i;-7-3i;-7-5i;-7-7i;-5+7i;-5+5i;-5+3i;-5+1i;-5-1i;-5-3i;-5-5i;-5-7i;-3+7i;-3+5i;-3+3i;-3+1i;-3-1i;-3-3i;-3-5i;-3-7i;-1+7i;-1+5i;-1+3i;-1+1i;-1-1i;-1-3i;-1-5i;-1-7i;1+7i;1+5i;1+3i;1+1i;1-1i;1-3i;1-5i;1-7i;3+7i;3+5i;3+3i;3+1i;3-1i;3-3i;3-5i;3-7i;5+7i;5+5i;5+3i;5+1i;5-1i;5-3i;5-5i;5-7i;7+7i;7+5i;7+3i;7+1i;7-1i;7-3i;7-5i;7-7i]);
    case 'QAM128_OLD'; hmod = modem.qammod('M', 128);
    case 'QAM128'; hmod.m = 128; hmod.modulate = @(x) genqammod(x, [-7+9i;-7+11i;-1+11i;-1+9i;-1-9i;-1-11i;-7-11i;-7-9i;-5+9i;-5+11i;-3+11i;-3+9i;-3-9i;-3-11i;-5-11i;-5-9i;-11+7i;-11+5i;-11+3i;-11+1i;-11-1i;-11-3i;-11-5i;-11-7i;-9+7i;-9+5i;-9+3i;-9+1i;-9-1i;-9-3i;-9-5i;-9-7i;-7+7i;-7+5i;-7+3i;-7+1i;-7-1i;-7-3i;-7-5i;-7-7i;-5+7i;-5+5i;-5+3i;-5+1i;-5-1i;-5-3i;-5-5i;-5-7i;-3+7i;-3+5i;-3+3i;-3+1i;-3-1i;-3-3i;-3-5i;-3-7i;-1+7i;-1+5i;-1+3i;-1+1i;-1-1i;-1-3i;-1-5i;-1-7i;1+7i;1+5i;1+3i;1+1i;1-1i;1-3i;1-5i;1-7i;3+7i;3+5i;3+3i;3+1i;3-1i;3-3i;3-5i;3-7i;5+7i;5+5i;5+3i;5+1i;5-1i;5-3i;5-5i;5-7i;7+7i;7+5i;7+3i;7+1i;7-1i;7-3i;7-5i;7-7i;9+7i;9+5i;9+3i;9+1i;9-1i;9-3i;9-5i;9-7i;11+7i;11+5i;11+3i;11+1i;11-1i;11-3i;11-5i;11-7i;5+9i;5+11i;3+11i;3+9i;3-9i;3-11i;5-11i;5-9i;7+9i;7+11i;1+11i;1+9i;1-9i;1-11i;7-11i;7-9i]);
    case 'QAM256_OLD'; hmod = modem.qammod('M', 256, 'SymbolOrder', 'user-defined', 'SymbolMapping', [15 31 47 63 79 95 111 127 143 159 175 191 207 223 239 255 14 30 46 62 78 94 110 126 142 158 174 190 206 222 238 254 13 29 45 61 77 93 109 125 141 157 173 189 205 221 237 253 12 28 44 60 76 92 108 124 140 156 172 188 204 220 236 252 11 27 43 59 75 91 107 123 139 155 171 187 203 219 235 251 10 26 42 58 74 90 106 122 138 154 170 186 202 218 234 250 9 25 41 57 73 89 105 121 137 153 169 185 201 217 233 249 8 24 40 56 72 88 104 120 136 152 168 184 200 216 232 248 7 23 39 55 71 87 103 119 135 151 167 183 199 215 231 247 6 22 38 54 70 86 102 118 134 150 166 182 198 214 230 246 5 21 37 53 69 85 101 117 133 149 165 181 197 213 229 245 4 20 36 52 68 84 100 116 132 148 164 180 196 212 228 244 3 19 35 51 67 83 99 115 131 147 163 179 195 211 227 243 2 18 34 50 66 82 98 114 130 146 162 178 194 210 226 242 1 17 33 49 65 81 97 113 129 145 161 177 193 209 225 241 0 16 32 48 64 80 96 112 128 144 160 176 192 208 224 240]);
