            61+63i;61+61i;61+59i;61+57i;61+55i;61+53i;61+51i;61+49i;61+47i;61+45i;61+43i;61+41i;61+39i;61+37i;61+35i;61+33i;61+31i;61+29i;61+27i;61+25i;61+23i;61+21i;61+19i;61+17i;61+15i;61+13i;61+11i;61+9i;61+7i;61+5i;61+3i;61+1i;61-1i;61-3i;61-5i;61-7i;61-9i;61-11i;61-13i;61-15i;61-17i;61-19i;61-21i;61-23i;61-25i;61-27i;61-29i;61-31i;61-33i;61-35i;61-37i;61-39i;61-41i;61-43i;61-45i;61-47i;61-49i;61-51i;61-53i;61-55i;61-57i;61-59i;61-61i;61-63i;63+63i;63+61i;63+59i;63+57i;63+55i;63+53i;63+51i;63+49i;63+47i;63+45i;63+43i;63+41i;63+39i;63+37i;63+35i;63+33i;63+31i;63+29i;63+27i;63+25i;63+23i;63+21i;63+19i;63+17i;63+15i;63+13i;63+11i;63+9i;63+7i;63+5i;63+3i;63+1i;63-1i;63-3i;63-5i;63-7i;63-9i;63-11i;63-13i;63-15i;63-17i;63-19i;63-21i;63-23i;63-25i;63-27i;63-29i;63-31i;63-33i;63-35i;63-37i;63-39i;63-41i;63-43i;63-45i;63-47i;63-49i;63-51i;63-53i;63-55i;63-57i;63-59i;63-61i;63-63i]);
    case 'APSK16'
        r12 = 2.6;
        cst = [exp(1j*2*pi*[0.5:1:3.5]/4) exp(1i*2*pi*(0.5:1:11.5)/12)*r12];
        % scramble the constellation to avoid strange spectra
        for cnt = 1:2:length(cst)/2
            k = cnt;
            i = cnt + length(cst)/2;
            tmp = cst(i); cst(i) = cst(k); cst(k) = tmp;
        end
        hmod.m = 16; hmod.modulate = @(x) genqammod(x, cst);
    case 'APSK32'
        r12 = 2.84; r13 = 5.27;
        cst = [exp(1i*2*pi*[0.5:1:3.5]/4) exp(1i*2*pi*(0.5:1:11.5)/12)*r12 ...
            exp(1i*2*pi*[0:15]/16)*r13];
        % scramble the constellation to avoid strange spectra
        for cnt = 1:2:length(cst)/2
            k = cnt;
            i = cnt + length(cst)/2;
            tmp = cst(i); cst(i) = cst(k); cst(k) = tmp;
        end
        hmod.m = 32; hmod.modulate = @(x) genqammod(x, cst);
    case 'PAM4_OLD'
        hmod = modem.pammod('M', 4, 'SymbolOrder', 'user-defined', 'SymbolMapping', [3 1 0 2]);
    case 'PAM4'
        hmod.m = 4; hmod.modulate = @(x) genqammod(x, [1;-1;3;-3]);
    case 'CPM_OLD'
        hmod = modem.pskmod(2);
    case 'CPM'
        hmod.m = 2; hmod.modulate = @(x) pskmod(x, hmod.m);
        iscpm = 1;       
    
	% added by Tom Wychock tom.wychock@keysight.com
    % creates a QAM 8 signal (use this setup to create more custom signals)
    case 'QAM8'
        y1Mag = 1;
        y1States = 4;
        y1Phase = 0;
        
        y2Mag = 2;
        y2States = 4;
        y2Phase = pi/4;
        
        cst = [exp(1i*(2*pi*[0:y1States-1]/y1States+y1Phase))*y1Mag...
               exp(1i*(2*pi*[0:y2States-1]/y2States+y2Phase))*y2Mag];
        % scramble the constellation (Tom Wychock)
        maproute = [1 6 4 5 7 2 8 3];
        cst = cst(maproute);
        % hmod = modem.genqammod('Constellation', cst, 'InputType', 'integer');
        hmod.m = 8; hmod.modulate = @(x) genqammod(x, cst);
    case 'OOK';hmod.m = 2; hmod.modulate = @(x) genqammod(x, [0+0i;1+0i]);
        
    otherwise; error('unknown modulation type');
end

if (plotConstellation)
    figure(3); clf;
    s = hmod.modulate(0:(hmod.m-1));
    plot(s, 'rx');
    title(['Constellation diagram for ' modType]);
    for n = 1:hmod.m
        if (hmod.m > 64)
            sym = dec2hex(n-1, ceil(ceil(log2(hmod.m))/4));
        else
            sym = dec2bin(n-1, ceil(log2(hmod.m)));
        end
        text(real(s(n)), imag(s(n)) + 0.1, sym, ...
            'FontSize', 8, 'FontUnits', 'points', 'HorizontalAlignment', 'center', 'VerticalAlignment', 'bottom');
    end
    xmin = min(real(s)) - 1;
    xmax = max(real(s)) + 1;
    ymin = min(imag(s)) - 1;
    ymax = max(imag(s)) + 1;
